mapper.insert:
INSERT INTO shop_cart
(user_id, count, cid, commodity_title, comment_picture, color_name, size_name, present_price)
VALUES (#{shopCart.userId},#{shopCart.count},#{shopCart.cid},#{shopCart.commodityTitle},#{shopCart.commentPicture},#{shopCart.colorName},
#{shopCart.sizeName},#{shopCart.presentPrice}
)

mapper.select:
SELECT cart_id,count
FROM shop_cart
WHERE user_id=#{userId}
AND csid=#{csid}
AND status = 1

mapper.update:
UPDATE shop_cart
SET count = #{count}
WHERE cart_id = #{cartId}
